function myFunction() {
  // Função principal para criar o quiz
  function createQuizFromTemplate() {

    var templateId = "11WgF3vFTaQRQcWndL22l0LxZhhp-1Zd2ErRZAWuL5RU";
    var atividade = "Atividade 1"
    var idpasta = "14JSc2kbWeriSIgv3YayWbkxJQ-uTNFhW" //pasta destino

    var rootFolder = DriveApp.getFolderById(idpasta);
    var templateFile = DriveApp.getFileById(templateId);
    var copiedFile = templateFile.makeCopy(atividade, rootFolder);
    var copiedForm = FormApp.openById(copiedFile.getId());

    // Modificar o formulário duplicado
    copiedForm.setTitle(atividade)
              .setShuffleQuestions(true)
              .setProgressBar(true)
              .setAcceptingResponses(true)
              .setShowLinkToRespondAgain(true);

    var matriculas = [
      'INF220009', 'INF230012', 'INF220029', 'INF230040', 'INF220022',
      'INF220014', 'INF230001', 'INF230011', 'INF230027', 'INF230005',
      'INF230003', 'INF220021', 'INF220003', 'INF220005', 'INF220001',
      'INF230002', 'INF220016', 'INF230051', 'INF220020', 'INF220013',
      'INF230018', 'INF230025', 'INF230009', 'INF220027', 'INF220028'
    ];

    var item = copiedForm.addMultipleChoiceItem();
item.setTitle('Escolha sua matrícula:')
    .setChoices(matriculas.map(function(matricula) {
        return item.createChoice(matricula);
    }))
    .setRequired(true);

copiedForm.addTextItem()
          .setTitle('Digite seu nome')
          .setRequired(true);

copiedForm.addPageBreakItem()
          .setTitle(atividade)
          .setHelpText('Responda ao questionário a seguir.');

// Lista de perguntas para o questionário
var questions = [
    {
        title: '1. O que caracteriza um Service Desk?',
        choices: [
            { text: 'Suporte físico aos usuários', correct: false },
            { text: 'Central de vendas e marketing', correct: false },
            { text: 'Hub centralizado para suporte a usuários', correct: true },
            { text: 'Canal exclusivo para desenvolvedores', correct: false },
            { text: 'Repositório de hardware', correct: false }
        ]
    },
    {
        title: '2. Qual das funções é típica do Help Desk?',
        choices: [
            { text: 'Gestão de mudanças', correct: false },
            { text: 'Planejamento de recursos', correct: false },
            { text: 'Atendimento reativo a incidentes', correct: true },
            { text: 'Desenvolvimento de software', correct: false },
            { text: 'Gerência de projetos', correct: false }
        ]
    },
    {
        title: '3. O que é um SLA?',
        choices: [
            { text: 'Lista de chamados pendentes', correct: false },
            { text: 'Acordo de Nível de Serviço', correct: true },
            { text: 'Relatório de satisfação do cliente', correct: false },
            { text: 'Sistema de licenciamento', correct: false },
            { text: 'Ferramenta de backup', correct: false }
        ]
    },
    {
        title: '4. Qual das opções representa uma boa prática de comunicação?',
        choices: [
            { text: 'Falar rapidamente para ganhar tempo', correct: false },
            { text: 'Usar jargões técnicos sempre', correct: false },
            { text: 'Ser claro, direto e educado', correct: true },
            { text: 'Ignorar dúvidas simples do usuário', correct: false },
            { text: 'Usar linguagem técnica para demonstrar conhecimento', correct: false }
        ]
    },
    {
        title: '5. A escuta ativa envolve:',
        choices: [
            { text: 'Ignorar interrupções', correct: false },
            { text: 'Corrigir o usuário constantemente', correct: false },
            { text: 'Ouvir, fazer perguntas e repetir informações-chave', correct: true },
            { text: 'Focar apenas no problema técnico', correct: false },
            { text: 'Anotar sem interagir', correct: false }
        ]
    },
    {
        title: '6. Como deve ser a linguagem usada com o usuário?',
        choices: [
            { text: 'Sempre informal', correct: false },
            { text: 'Técnica e complexa', correct: false },
            { text: 'Profissional e acessível', correct: true },
            { text: 'Ríspida para mostrar autoridade', correct: false },
            { text: 'Totalmente informal', correct: false }
        ]
    },
    {
        title: '7. O que é a Matriz GUT usada para?',
        choices: [
            { text: 'Gerar relatórios gerenciais', correct: false },
            { text: 'Cálculo de desempenho financeiro', correct: false },
            { text: 'Priorização de chamados', correct: true },
            { text: 'Avaliação de senhas', correct: false },
            { text: 'Medição de satisfação do cliente', correct: false }
        ]
    },
    {
        title: '8. Quando escalar um chamado ao Nível 2?',
        choices: [
            { text: 'Quando for um problema de senha', correct: false },
            { text: 'Quando o usuário pedir', correct: false },
            { text: 'Quando o problema exigir conhecimento técnico especializado', correct: true },
            { text: 'Quando o sistema estiver funcionando normalmente', correct: false },
            { text: 'Sempre que houver fila', correct: false }
        ]
    },
    {
        title: '9. Qual ferramenta é líder em ITSM?',
        choices: [
            { text: 'ServiceNow', correct: true },
            { text: 'GLPI', correct: false },
            { text: 'Excel', correct: false },
            { text: 'WhatsApp', correct: false },
            { text: 'OTRS', correct: false }
        ]
    },
    {
        title: '10. O ciclo PDCA é usado para:',
        choices: [
            { text: 'Registrar chamados', correct: false },
            { text: 'Apagar arquivos duplicados', correct: false },
            { text: 'Cadastrar usuários', correct: false },
            { text: 'Criar senhas', correct: false },
            { text: 'Melhorar continuamente processos', correct: true }
        ]
    },
    {
        title: '11. O que significa o “Princípio do Menor Privilégio”?',
        choices: [
            { text: 'Garantir que cada usuário tenha apenas o acesso necessário', correct: true },
            { text: 'Conceder acesso conforme o cargo', correct: false },
            { text: 'Dar acesso total a todos os usuários', correct: false },
            { text: 'Liberar acesso em horários comerciais', correct: false },
            { text: 'Compartilhar senhas para agilizar o atendimento', correct: false }
        ]
    },
    {
        title: '12. Um chatbot no atendimento ajuda principalmente a:',
        choices: [
            { text: 'Evitar chamadas telefônicas', correct: false },
            { text: 'Automatizar tarefas repetitivas', correct: true },
            { text: 'Reunir a equipe técnica', correct: false },
            { text: 'Monitorar redes', correct: false },
            { text: 'Substituir permanentemente os atendentes', correct: false }
        ]
    },
    {
        title: '13. Em uma crise de alta severidade, o que deve ser feito primeiro?',
        choices: [
            { text: 'Enviar um e-mail para o cliente', correct: false },
            { text: 'Encerrar os serviços temporariamente', correct: false },
            { text: 'Realizar pesquisa de satisfação', correct: false },
            { text: 'Classificar a severidade e acionar a equipe técnica', correct: true },
            { text: 'Esperar estabilizar', correct: false }
        ]
    },
    {
        title: '14. Qual metodologia ágil ajuda na gestão visual de chamados?',
        choices: [
            { text: 'Scrum', correct: false },
            { text: 'Kanban', correct: true },
            { text: 'DevOps', correct: false },
            { text: 'ITIL', correct: false },
            { text: 'MVP', correct: false }
        ]
    },
    {
        title: '15. O que é CSAT?',
        choices: [
            { text: 'Ferramenta de backup', correct: false },
            { text: 'Indicador de satisfação imediata', correct: true },
            { text: 'Sistema de chamados', correct: false },
            { text: 'Tipo de incidente', correct: false },
            { text: 'Modelo de rede corporativa', correct: false }
        ]
    },
    {
        title: '16. Qual é o objetivo principal do ITIL?',
        choices: [
            { text: 'Criar softwares personalizados', correct: false },
            { text: 'Reduzir o número de chamados', correct: false },
            { text: 'Oferecer um serviço de TI eficiente e com valor para o cliente', correct: true },
            { text: 'Automatizar o atendimento via bots', correct: false },
            { text: 'Eliminar a necessidade de suporte técnico', correct: false }
        ]
    },
    {
        title: '17. O que deve conter na documentação de um incidente?',
        choices: [
            { text: 'Somente o nome do atendente', correct: false },
            { text: 'A descrição do erro sem detalhes', correct: false },
            { text: 'Apenas o horário da chamada', correct: false },
            { text: 'Problema, ações tomadas e solução', correct: true },
            { text: 'Feedback do usuário', correct: false }
        ]
    },
    {
        title: '18. Qual dessas ferramentas é open-source?',
        choices: [
            { text: 'ServiceNow', correct: false },
            { text: 'Freshservice', correct: false },
            { text: 'Microsoft Teams', correct: false },
            { text: 'Zendesk', correct: false },
            { text: 'GLPI', correct: true }
        ]
    },
    {
        title: '19. O que é necessário para um bom diagnóstico de problema?',
        choices: [
            { text: 'Resolver sem verificar os detalhes', correct: false },
            { text: 'Testar imediatamente qualquer solução', correct: false },
            { text: 'Coletar informações, reproduzir o erro e isolar a causa', correct: true },
            { text: 'Consultar sempre o usuário final', correct: false },
            { text: 'Utilizar somente o histórico', correct: false }
        ]
    },
    {
        title: '20. O que é a técnica dos 5 porquês?',
        choices: [
            { text: 'Ferramenta de priorização de tarefas', correct: false },
            { text: 'Método de mensuração de produtividade', correct: false },
            { text: 'Técnica para descobrir a causa raiz de um problema', correct: true },
            { text: 'Processo de avaliação de atendimento', correct: false },
            { text: 'Forma de validar SLAs', correct: false }
        ]
    },
    {
        title: '21. Como o feedback dos usuários deve ser usado?',
        choices: [
            { text: 'Para demitir funcionários', correct: false },
            { text: 'Para justificar falhas', correct: false },
            { text: 'Para identificar melhorias e tendências', correct: true },
            { text: 'Para cancelar chamados', correct: false },
            { text: 'Para evitar novas solicitações', correct: false }
        ]
    },
    {
        title: '22. Qual é a vantagem do uso de ferramentas de acesso remoto?',
        choices: [
            { text: 'Evita a necessidade de qualquer documentação', correct: false },
            { text: 'Aumenta o tempo de resolução', correct: false },
            { text: 'Permite resolver problemas sem deslocamento', correct: true },
            { text: 'Garante que o usuário não precise relatar nada', correct: false },
            { text: 'Elimina a necessidade de treinamento', correct: false }
        ]
    },
    {
        title: '23. Qual prática contribui para um ambiente de melhoria contínua?',
        choices: [
            { text: 'Executar os mesmos processos indefinidamente', correct: false },
            { text: 'Esperar ordens para propor mudanças', correct: false },
            { text: 'Envolver toda a equipe em pequenas melhorias diárias', correct: true },
            { text: 'Aguardar crises para agir', correct: false },
            { text: 'Criar relatórios mensais apenas', correct: false }
        ]
    },
    {
        title: '24. Qual ferramenta é indicada para equipes que já usam o Jira?',
        choices: [
            { text: 'GLPI', correct: false },
            { text: 'ServiceNow', correct: false },
            { text: 'Zendesk', correct: false },
            { text: 'Jira Service Management', correct: true },
            { text: 'Zabbix', correct: false }
        ]
    },
    {
        title: '25. O que define a prioridade de um chamado?',
        choices: [
            { text: 'O humor do atendente', correct: false },
            { text: 'O tempo que está na fila', correct: false },
            { text: 'O impacto e a urgência do problema', correct: true },
            { text: 'O nome do solicitante', correct: false },
            { text: 'O tipo de dispositivo', correct: false }
        ]
    },
    {
        title: '26. Quando envolver o Nível 3 de suporte?',
        choices: [
            { text: 'Sempre que houver fila no Nível 1', correct: false },
            { text: 'Quando o problema for repetitivo', correct: false },
            { text: 'Para problemas com impacto alto e alta complexidade', correct: true },
            { text: 'Em incidentes de senha', correct: false },
            { text: 'Para cadastrar novos usuários', correct: false }
        ]
    },
    {
        title: '27. Qual dessas práticas é parte da segurança da informação?',
        choices: [
            { text: 'Compartilhamento de contas', correct: false },
            { text: 'Backup regular de dados críticos', correct: true },
            { text: 'Uso de senhas simples para agilizar', correct: false },
            { text: 'Acesso irrestrito para todos', correct: false },
            { text: 'Instalação de softwares sem antivírus', correct: false }
        ]
    },
    {
        title: '28. Qual o objetivo da pesquisa NPS?',
        choices: [
            { text: 'Calcular o tempo de atendimento', correct: false },
            { text: 'Medir a lealdade dos usuários', correct: true },
            { text: 'Identificar problemas técnicos', correct: false },
            { text: 'Atualizar o sistema', correct: false },
            { text: 'Planejar mudanças estruturais', correct: false }
        ]
    },
    {
        title: '29. Qual dessas é uma prática recomendada durante uma crise?',
        choices: [
            { text: 'Interromper a comunicação com usuários', correct: false },
            { text: 'Evitar dar prazos', correct: false },
            { text: 'Usar canais oficiais e manter todos informados', correct: true },
            { text: 'Aguardar o fim do incidente para comunicar', correct: false },
            { text: 'Compartilhar informações sigilosas', correct: false }
        ]
    },
    {
        title: '30. O que representa a metodologia Scrum no Service Desk?',
        choices: [
            { text: 'Elaboração de manuais técnicos', correct: false },
            { text: 'Uso de reuniões rápidas para alinhamento da equipe', correct: true },
            { text: 'Planejamento de orçamento', correct: false },
            { text: 'Criação de relatórios semanais', correct: false },
            { text: 'Abertura de chamados via app', correct: false }
        ]
    }
];

    // Adiciona as perguntas ao formulário
    questions.forEach(function(q) {
      var item = copiedForm.addMultipleChoiceItem();
      item.setTitle(q.title);
      var choices = q.choices.map(function(choice) {
        return item.createChoice(choice.text, choice.correct);
      });
      item.setChoices(choices);
      item.setPoints(1); // Define pontuação por pergunta
      item.setRequired(true);
    });

    // Exibir o link para edição do formulário no Logger
    Logger.log("Quiz criado: " + copiedForm.getEditUrl());
  }

  // Chamar a função para criar o quiz
  createQuizFromTemplate();
}
